      const isValidPlan =
        Array.isArray(plan) && plan.every((p) => typeof p.action === "string");
      if (isValidPlan) {
        console.log("🗺 계획:", plan);
        await executeStructuredPlan(plan);
      }
    } catch (e) {
      console.error("⚠️ 계획 생성 중 오류 발생:", e);
